import { useState } from "react"
import { NavLink } from "react-router-dom"
import { AppContext } from "../context/AppContext"
import Comment from "./Comment"
import { useContext } from "react"
import toast from "react-hot-toast"

const API_URL = process.env.REACT_APP_URL

function Card({ blog }) {

    const {fetchBlogs} =useContext(AppContext)

    const [form, setform] = useState({
        title: "",
        body: ""
    })

    function formHandler(event) {
        const { name, value } = event.target
        setform(prev => {
            return {
                ...prev,
                [name]: value
            }
        })
    }

    async function submitHandler(event){
        event.preventDefault()
        if(!form.title || !form.body){
            toast.error("Please Fill In All Details")
            return
        }
        console.log(form)
        await fetch(`${API_URL}/posts/create`,{
            method: 'POST',
            headers:{
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                title:form.title,
                body:form.body
            })
        })
        toast('Comment posted successfully.', {
            icon: 'üëè',
          });
        fetchBlogs()
    }


    return (
        <div className="border flex justify-center w-[60vw] p-4 m-4 flex-col gap-2">
            <NavLink to={`/${blog._id}`}><h1 className="font-bold text-2xl hover:underline">{blog.title}</h1></NavLink>
            <p className="ml-1">{blog.body}</p>

            <form className="flex gap-2">
                <input name="title" required value={form.title} onChange={formHandler} className="w-4/12 border p-2" placeholder="Enter your name"/>
                <input name="body" required value={form.body} onChange={formHandler} className="w-6/12 p-2 border" placeholder="Enter your comment" />
                <button className="bg-blue-700 text-white font-bold text-center p-2 border rounded-md w-2/12" onClick={submitHandler}>Post</button>
            </form>

        </div>
    )
}

export default Card
