// import { useState } from "react"
import { NavLink } from "react-router-dom"

function Card({ blog }) {

    const [comment, setComments]=useState([])

    const comments = blog.comments

    console.log(comments)

    return (
        <div className="border flex justify-center w-[60vw] p-4 m-4 flex-col gap-2">
            <NavLink><h1 className="font-bold text-2xl hover:underline">{blog.title}</h1></NavLink>
            <p>{blog.body}</p>
            <div>
                <p className="font-bold">Comments:</p>
                {
                    comments.length > 0 ? (
                        comments.map((comment) => {
                            return (
                                <div className="ml-2 flex gap-4">
                                    <p className="font-semibold">{comment.user}:</p>
                                    <p>{comment.body}</p>
                                </div>
                            )
                        })
                    ) : (
                        <p>No Comments Available</p>
                    )
                }
            </div>
            <form>
                <input name="comment" value={comment} onChange={(event)=>setComments(event.target.value)} className="w-10/12 p-2" placeholder="Enter your comment"/>
            </form>
        </div>
    )
}

export default Card
