import { useState } from "react"
import { NavLink } from "react-router-dom"

const API_URL = process.env.REACT_APP_URL

function Card({ blog }) {

    const [form, setform] = useState({
        user: "",
        body: ""
    })

    function formHandler(event) {
        const { name, value } = event.target
        setform(prev => {
            return {
                prev,
                [name]: value
            }
        })
    }

    function submitHandler(){
        fetch(`${API_URL}/create/comment`,{
            method: 'POST',

        })
    }

    const comments = blog.comments

    console.log(comments)

    return (
        <div className="border flex justify-center w-[60vw] p-4 m-4 flex-col gap-2">
            <NavLink><h1 className="font-bold text-2xl hover:underline">{blog.title}</h1></NavLink>
            <p>{blog.body}</p>
            <div>
                <p className="font-bold">Comments:</p>
                {
                    comments.length > 0 ? (
                        comments.map((comment) => {
                            return (
                                <div className="ml-2 flex gap-4">
                                    <p className="font-semibold">{comment.user}:</p>
                                    <p>{comment.body}</p>
                                </div>
                            )
                        })
                    ) : (
                        <p>No Comments Available</p> 
                    )
                }
            </div>
            <form className="flex gap-2">
                <input name="user" value={form.name} onChange={formHandler} className="w-4/12 border p-2" placeholder="Enter your name"/>
                <input name="body" value={form.comment} onChange={formHandler} className="w-6/12 p-2 border" placeholder="Enter your comment" />
                <button className="bg-blue-700 text-white font-bold text-center p-2 border rounded-md w-2/12" onClick={submitHandler}>Post</button>
            </form>
        </div>
    )
}

export default Card
