import { useState } from "react";
import { NavLink } from "react-router-dom";

const API_URL = process.env.REACT_APP_URL;

function Card({ blog }) {
    const [form, setForm] = useState({
        post: blog._id,
        user: "",
        body: ""
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    function formHandler(event) {
        const { name, value } = event.target;
        setForm((prev) => ({
            ...prev,
            [name]: value
        }));
    }

    async function submitHandler(event) {
        event.preventDefault();
        
        // Simple validation
        if (!form.user || !form.body) {
            setError("Both fields are required");
            return;
        }

        setLoading(true);
        setError(null); // Reset any previous error

        try {
            const response = await fetch(`${API_URL}/create/comment`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(form)
            });

            if (!response.ok) {
                throw new Error("Failed to submit comment");
            }

            // Optionally, reset the form fields
            setForm({ post: blog._id, user: "", body: "" });
            // Optionally, you could also refresh comments or update state here

        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    }

    const comments = blog.comments;

    return (
        <div className="border flex justify-center w-[60vw] p-4 m-4 flex-col gap-2">
            <NavLink to={`/blog/${blog._id}`}><h1 className="font-bold text-2xl hover:underline">{blog.title}</h1></NavLink>
            <p>{blog.body}</p>
            <div>
                <p className="font-bold">Comments:</p>
                {comments.length > 0 ? (
                    comments.map((comment) => (
                        <div key={comment._id} className="ml-2 flex gap-4">
                            <p className="font-semibold">{comment.user}:</p>
                            <p>{comment.body}</p>
                        </div>
                    ))
                ) : (
                    <p>No Comments Available</p>
                )}
            </div>
            {error && <p className="text-red-500">{error}</p>}
            <form className="flex gap-2" onSubmit={submitHandler}>
                <input
                    name="user"
                    value={form.user}
                    onChange={formHandler}
                    className="w-4/12 border p-2"
                    placeholder="Enter your name"
                />
                <input
                    name="body"
                    value={form.body}
                    onChange={formHandler}
                    className="w-6/12 p-2 border"
                    placeholder="Enter your comment"
                />
                <button
                    type="submit"
                    className={`bg-blue-700 text-white font-bold text-center p-2 border rounded-md w-2/12 ${loading ? 'opacity-50 cursor-not-allowed' : ''}`}
                    disabled={loading}
                >
                    {loading ? "Posting..." : "Post"}
                </button>
            </form>
        </div>
    );
}

export default Card;
