import { useState } from "react"
import { NavLink } from "react-router-dom"
import { AppContext } from "../context/AppContext"
import Comment from "./Comment"
import { useContext } from "react"
import toast from "react-hot-toast"

const API_URL = process.env.REACT_APP_URL

function Card({ blog }) {

    const {fetchBlogs} =useContext(AppContext)

    const [form, setform] = useState({
        post: blog._id,
        user: "",
        body: ""
    })

    function formHandler(event) {
        const { name, value } = event.target
        setform(prev => {
            return {
                ...prev,
                [name]: value
            }
        })
    }

    async function submitHandler(event){
        event.preventDefault()
        if(!form.user || !form.body){
            toast.error("Please Fill In All Details")
            return
        }
        console.log(form)
        await fetch(`${API_URL}/comment/create`,{
            method: 'POST',
            headers:{
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                post:form.post,
                user:form.user,
                body:form.body
            })
        })
        toast('Comment posted successfully.', {
            icon: 'üëè',
          });
        fetchBlogs()
    }


    return (
        <div className="border flex justify-center w-[60vw] p-4 m-4 flex-col gap-2">
            <NavLink to={`/${blog._id}`}><h1 className="font-bold text-2xl hover:underline">{blog.title}</h1></NavLink>
            <p className="ml-1 line-clamp-2 my-2">{blog.body}</p>
            
            <Comment blog={blog}/>

        </div>
    )
}

export default Card
