import { AppContext } from "../context/AppContext"
import Card from "./Card"
import Comment from "./Comment"
import { useContext, useState } from "react"
import {toast} from "react-hot-toast"

const API_URL = process.env.REACT_APP_URL

function Blog({ blog }) {

    const {fetchBlog} =useContext(AppContext)

    const [form, setform] = useState({
        title: "",
        body: ""
    })

    function formHandler(event) {
        const { name, value } = event.target
        setform(prev => {
            return {
                ...prev,
                [name]: value
            }
        })
    }

    async function submitHandler(event) {
        event.preventDefault()
        if (!form.title || !form.body) {
            toast.error("Please Fill In All Details")
            return
        }
        console.log(form)
        await fetch(`${API_URL}/posts/create`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                title: form.title,
                body: form.body
            })
        })
        toast('Blog posted successfully.', {
            icon: 'üëè',
        });
        fetchBlog()
    }


    return (
        <div className="flex flex-col justify-center items-center mt-10">
            <div className="border flex justify-center w-[60vw] p-4 m-4 flex-col gap-2">
            <NavLink to={`/${blog._id}`}><h1 className="font-bold text-2xl hover:underline">{blog.title}</h1></NavLink>
            <p className="ml-1">{blog.body}</p>
            
            <Comment comments={blog.comments}/>

            <form className="flex gap-2">
                <input name="user" required value={form.user} onChange={formHandler} className="w-4/12 border p-2" placeholder="Enter your name"/>
                <input name="body" required value={form.body} onChange={formHandler} className="w-6/12 p-2 border" placeholder="Enter your comment" />
                <button className="bg-blue-700 text-white font-bold text-center p-2 border rounded-md w-2/12" onClick={submitHandler}>Post</button>
            </form>

        </div>

            <Comment comments={blog.comments} />

            <form className="flex gap-2 flex-col">
                <input name="title" required value={form.title} onChange={formHandler} className="border p-2" placeholder="Enter Blog Title" />
                <input name="body" required value={form.body} onChange={formHandler} className="p-2 border" placeholder="Enter Blog Body" />
                <button className="bg-blue-700 text-white font-bold text-center p-2 border rounded-md" onClick={submitHandler}>Post</button>
            </form>
        </div>
    )
}

export default Blog
