function Comment({comments}){
    return (
        <div>
                <p className="font-bold">Comments:</p>
                {
                    comments && comments.length > 0 ? (
                        comments.map((comment) => {
                            return (
                                <div key={comment._id} className="ml-2 flex gap-4">
                                    <p className="font-semibold">{comment.user}:</p>
                                    <p>{comment.body}</p>
                                </div>
                            )
                        })
                    ) : (
                        <p>No Comments Available</p> 
                    )
                }
                <form className="flex gap-2">
                    <input name="user" required value={form.user} onChange={formHandler} className="w-4/12 border p-2" placeholder="Enter your name" />
                    <input name="body" required value={form.body} onChange={formHandler} className="w-6/12 p-2 border" placeholder="Enter your comment" />
                    <button className="bg-blue-700 text-white font-bold text-center p-2 border rounded-md w-2/12" onClick={submitHandler}>Post</button>
                </form>
            </div>
    )
}   

export default Comment
